import "./App.css";
import React, { Component } from "react";
import axios from "axios";

class App extends Component {
  constructor() {
    super();
    this.state = {
      breed: "husky",
      images: []
    };
  }

  componentDidMount() {
    this.fetchDogImages();
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.breed !== this.state.breed) {
      this.setState({
        images: []
      });
      this.fetchDogImages();
    }
  }

  fetchDogImages(){
    axios
      .get(`https://dog.ceo/api/breed/${this.state.breed}/images`)
      .then(result => {
        this.setState({
          images: result.data.message
        })}
        )
      .catch(error => {
        console.log("error:", error);
      })
  }

  handleChange(event){
    this.setState({
      breed: event.target.value
    })
  }

  render() {
    return (
      <div>
        <h1>The Dog Website</h1>

        <select value={this.state.breed} onChange={this.handleChange}>
          <option value="collie-border">Border Collie</option>
          <option value="heeler-blue">Blue Heeler</option>
          <option value="husky">Husky</option>
        </select>
        <div>
          {this.state.images.map((image, index) => {
            return <img key={index} src={image} alt="dog" />
          })}
        </div>
      </div>
    );
  }
}

export default App;
